#dictionary_to_list function
def dictionary_to_list(book_dataset: dict[str, list[dict[str, object]]]) -> list[dict[str, object]]:
    """
    Author: Zane Labonte-Hagar (101239002)
    Returns a list containing dictionaries of google book metadata, from a given
    google books dataset dictionary. 
    
    >>> dictionary_to_list(T004_load_data.load_dataset("Google_Books_Dataset.csv"))
    [{'title': "Antiques Roadkill: A Trash 'n' Treasures Mystery", 'author': 'Barbara Allan', 'language': 'English', 'rating': 3.3, 'publisher': 'Kensington Publishing Corp.', 'page_count': 288, 'generes': 'Fiction'}, {'title': 'The Painted Man (The Demon Cycle, Book 1)', 'author': 'Peter V. Brett', 'language': 'English', 'rating': 4.5, 'publisher': 'HarperCollins UK', 'page_count': 544, 'generes': 'Fiction'}, ...]
    >>> dictionary_to_list(T004_load_data.load_dataset("Google_Books_Dataset.csv"))
    [..., {'title': 'Management (The Brian Tracy Success Library)', 'author': 'Brian Tracy', 'language': 'English', 'rating': None, 'publisher': 'AMACOM', 'page_count': 112, 'generes': 'Management'}, {'title': 'The Secrets of Saving and Investing with Alvin Hall: Simple Strategies to Make Your Money Go Further', 'author': 'Alvin Hall', 'language': 'English', 'rating': 3.7, 'publisher': 'Hachette UK', 'page_count': 30, 'generes': 'Money Management'}, {'title': 'Becoming Steve Jobs: The Evolution of a Reckless Upstart into a Visionary Leader', 'author': 'Brent Schlender', 'language': 'English', 'rating': 4.6, 'publisher': 'Crown Business', 'page_count': 464, 'generes': 'Information Technology'}, {'title': 'The Secrets of Saving and Investing with Alvin Hall: Simple Strategies to Make Your Money Go Further', 'author': 'Alvin Hall', 'language': 'English', 'rating': 3.7, 'publisher': 'Hachette UK', 'page_count': 30, 'generes': 'Investing'}]    """
    
    sorting = []
    
    for genre, book_list in book_dataset.items():        
        for book in book_list:
            book = dict(book) # Make a copy of the book dictionary so that we don't modify the given dict
            book["generes"] = genre
            sorting.append(book)
            
    return sorting
    
